$("#my_postalcode").attr('maxlength','5');
$("#my_postalcode").attr('onkeyup','loadposkod()');
function loadposkod(){
	var poskod = $("#my_postalcode").val();
	var inputlenght = $("#my_postalcode").val().length;
	if(inputlenght==5){
		$( "<div id='loadhere'></div>" ).appendTo( "body" );
		$("#loadhere").load('mypostalcode/postal_code_db.php?poskod='+poskod);	
	}
	else if(inputlenght<5){
		$("#my_city").val("");
		$("#my_state").val("");
		
	}
	
}
